mapper.update:
/* updateEmergencynumber */
UPDATE employee_emergencynumber
set
emer_num = #{emerNum}
,employee_seq = #{employeeSeq}
,emp_name = #{empName}
,emp_rel = #{empRel}
,emer_edit = #{emerEdit}
WHERE seq = #{seq}

/* deleteEmergencynumber */
DELETE FROM employee_emergencynumber
WHERE seq = #{seq}
