mapper.insert:
 INSERT INTO inquiry
 (
  cs_seq,
  inquiry_date,
  inquiry_title,
  inquiry_content, 
  answer_date,
  answer_content
 )
 VALUES
	(
	 #{csSeq}
	,#{inquiryDate}
	,#{inquiryTitle}
	,#{inquiryContent}
	,#{answerDate}
	,#{answerContent}
	)

mapper.update:
/* updateInquiry */
UPDATE inquiry
set
cs_seq = #{csSeq}
,inquiry_date = #{inquiryDate}
,inquiry_title = #{inquiryTitle}
,inquiry_content = #{inquiryContent}
,answer_date = #{answerDate}
,answer_content = #{answerContent}
WHERE seq = #{seq}

/* deleteInquiry */
DELETE FROM inquiry
WHERE seq = #{seq}
